$(function(){$(".chat-item").mouseover(function(e){$(this).addClass('active')});$(".chat-item").mouseleave(function(e){$(this).removeClass("active")});function scrollConversationScreen(){$("textarea").focus();$('.content-main').scrollTop($('.content-main')[0].scrollHeight);}
scrollConversationScreen();$("textarea").keydown(function(e){if(e.keyCode==13){e.preventDefault();var content=$("textarea").val();if(!content||currentUser==activeUser){return;}
var sender=currentUser;var recipient=activeUser;var csrf_token=$("input[name='csrfmiddlewaretoken']").val();$.ajax({url:'/blog/message/sendmessage/',type:"POST",data:{'content':content,'sender':sender,'recipient':recipient,'csrfmiddlewaretoken':csrf_token},success:function(data){$("#send-message").before(data.message);$("textarea").val('');scrollConversationScreen();}})}})});;